////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
 * Restituisce Unix in millisecondi
 * @param {string | number | Date} date Data di riferimento, se Null restituisce la data Unix attuale
 * @param {boolean} seconds Unix in secondi, default false
 */
function GetUnix(date = null, seconds = false) {
    if (!date) return (seconds) ? Math.round(Date.now().valueOf() / 1000) : Date.now()
    if (typeof date === 'string' && !isNaN(date)) date = parseInt(date)
    if (typeof date === 'number') {
        if (date.toString().length < 13 && !seconds) date *= 1000
        date = Math.round(date)
    }
    return (seconds) ? Math.round(new Date(date).valueOf() / 1000) : new Date(date).valueOf()
}

/**
 * Restituisce la data e ora (Local Time)
 * @param {string | number | Date} date Data UTC di riferimento, se Null restituisce la data attuale
 * @param {boolean} en Formato inglese YYYY-MM-DD H:M:S
 */
function GetLocalDateTime(date = null, en = false) {
    date = GetUnix(date)
    date = new Date(date)

    let h = (new Intl.DateTimeFormat('it-IT', { timeStyle: 'long' }).format(date)).substr(0, 8).trim()
    let y = date.getFullYear()
    let m = (date.getMonth() + 1).toString()
    let d = (date.getDate()).toString()
    m = (m.length == 1) ? '0' + m : m
    d = (d.length == 1) ? '0' + d : d

    return (en) ? y + '-' + m + '-' + d + ' ' + h : d + '/' + m + '/' + y + ' ' + h
}

/**
 * Restituisce un Object con tutti i risultati (Local Time)
 * @param {string | number | Date} date_from Data UTC inizio
 * @param {string | number | Date} date_to Data UTC fine, se Null restituisce la data attuale
 */
function GetTimeElapsed(date_from, date_to = null) {
    date_from = GetUnix(date_from)
    date_from = new Date(date_from)
    date_to = GetUnix(date_to)
    date_to = new Date(date_to)

    var mls = Math.abs((date_from.getTime() - date_to.getTime()))
    mls = Math.round(mls)

    var milliseconds = mls / 1000
    var years = Math.floor(milliseconds / 31536000)
    var days = Math.floor((milliseconds % 31536000) / 86400)
    var hours = Math.floor(((milliseconds % 31536000) % 86400) / 3600)
    var minutes = Math.floor((((milliseconds % 31536000) % 86400) % 3600) / 60)
    var seconds = Math.floor((((milliseconds % 31536000) % 86400) % 3600) % 60)

    var levels = [
        [years, ((years > 1) ? 'anni' : 'anno')],
        [days, ((days > 1) ? 'giorni' : 'giorno')],
        [hours, ((hours > 1) ? 'ore' : 'ora')],
        [minutes, ((minutes > 1) ? 'minuti' : 'minuto')],
        [seconds, ((seconds > 1) ? 'secondi' : 'secondo')],
    ]

    var description = ''

    for (var i = 0, max = levels.length; i < max; i++) {
        if (levels[i][0] === 0) continue
        description += ' ' + levels[i][0] + ' ' + levels[i][1]
    }

    description = description.trim()
    description = (description == '') ? '1 secondo' : description

    return {
        start: date_from,
        end: date_to,
        description: description,
        years: years,
        days: Math.floor(mls / (1000 * 60 * 60 * 24)),
        hours: Math.floor(mls / (1000 * 60 * 60)),
        minutes: Math.floor(mls / (1000 * 60)),
        seconds: Math.floor(mls / 1000),
        milliseconds: mls
    }
}

/**
 * Restituisce un numero intero da una stringa di testo
 * @param {any} value Stringa di testo da valutare
 * @param {any} return_value Valore di default, null
 */
function GetNumber(value = null, return_value = 0) {
    if (value === null || value === undefined) { return return_value }
    if (typeof value === 'number') { return value }
    if (typeof value !== 'string') { return value }
    if (value.trim() === '') { return return_value }
    let number = parseInt(value)
    return (Number.isInteger(number)) ? number : return_value
}

/**
 * Verifica se un oggetto è di tipo String vuota e non contiene caratteri
 * @param {object} object Oggetto da verificare
 */
function IsStringEmpty(object) {
    if (object === null || object === undefined) return true
    return (typeof object === 'string' && object.trim().length === 0)
}

/**
 * Verifica se un oggetto è un Object Array
 * @param {object} object Oggetto da verificare
 */
function IsArray(object) {
    if (object === null || object === undefined) return false
    return (Array.isArray(object))
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Nome applicazioni*/
const APP_NAME = 'Power Control'

/**Versione applicazione*/
const APP_VERSION = '1.0.0 del 14/01/2023'

/**Autore applicazione*/
const AUTHOR_NAME = 'Bruno Leonardi'

/**Email autore*/
const AUTHOR_EMAIL = 'b.leonardi78@gmail.com'

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Tutti gli stati delle entità di Home Assistant*/
const SYSTEM_STATES = global.get('homeassistant.homeAssistant.states') || {}

/**Tutti i servizi di Home Assistant*/
const SYSTEM_SERVICES = global.get('homeassistant.homeAssistant.services') || {}

/**Stato servizio server*/
const SYSTEM_RUNNING = global.get('homeassistant.homeAssistant.isRunning') || false

/**Stato connessione server*/
const SYSTEM_CONNECTED = global.get('homeassistant.homeAssistant.isConnected') || false

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Indica se il controllo carichi è in funzione*/
var PC_ENABLED = false

/**Comando per attivare o disattivare il controllo carichi*/
var PC_COMMAND_ON_OFF = env.get("powercontrol_on_off") || ''
PC_COMMAND_ON_OFF = PC_COMMAND_ON_OFF.trim() || null

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Sensore generale per la lettura della potenza*/
var SENSOR_GENERAL = env.get("general_sensor") || ''
SENSOR_GENERAL = SENSOR_GENERAL.trim() || null

/**Potenza massima del contatore*/
const POWER_MAX = GetNumber(env.get("power_max"))

/**Potenza limite di sovraccarico*/
const POWER_LIMIT = GetNumber(env.get("power_limit"))

/**Ritardo riattivazione carichi*/
const DELAY_TURN_ON = GetNumber(env.get("delay_turn_on"), 10)

/**Ritardo disattivazione carichi*/
const DELAY_TURN_OFF = GetNumber(env.get("delay_turn_off"), 5)

/**Configurazione di Power Control*/
var CONFIGURATION = null

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Potenza attuale letta*/
var POWER_NOW = function () {
    let value = GetSystemEntity(SENSOR_GENERAL) || { state: 0 }
    return Math.round(value.state)
}

/**Potenza residua disponibile*/
var POWER_AVAILABLE = function () {
    let value = ((POWER_MAX - POWER_NOW()) <= 0) ? 0 : POWER_MAX - POWER_NOW()
    return Math.round(value)
}

/**Potenza di sovraccarico in base al limite impostato*/
var POWER_OVERLOAD = function () {
    let value = (POWER_NOW() > POWER_LIMIT) ? POWER_NOW() - POWER_LIMIT : 0
    return Math.round(value)
}

/**Indica se il limite è stato raggiunto o superato*/
var IS_OVERLOAD = function () {
    return (POWER_NOW() > POWER_LIMIT || POWER_NOW() >= POWER_MAX)
}

/**Indica se il sovraccarico è uguale o superiore alla potenza del contatore*/
var IS_OVERLOAD_CRITICAL = function () {
    return (POWER_NOW() >= POWER_MAX)
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Struttura Array di entità da controllare in ordine di distacco [{"power":"entity.name","load":"entity.name"}]*/
var ENTITIES = [{
    power: null,
    load: null,
    name: null,
    restore: true,
    state: 'off',
    energy: null
}]; ENTITIES = env.get("entities") || []

/**Numero di entità impostate*/
var ENTITIES_COUNT = ENTITIES.length

/**Numero di entità impostate e valide*/
var ENTITIES_VALID = 0

/**Lista di entità configurate*/
var ENTITIES_LIST = 'Nessun carico'

/**Energia delle entità*/
var ENTITIES_ENERGY_CONSUMPTION = 0

/**Energia delle entità sospesa*/
var ENTITIES_ENERGY_PENDING = 0

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**Domini disponibili per il controllo*/
const DOMAIN_AVAILABLE = {
    'switch': 'switch',
    'light': 'light',
    'fan': 'fan',
    'climate': 'climate',
    'media_player': 'media_player'
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
 * Accende il carico
* @param {string} entity_id Entità di Home Assistant
*/
function TurnOn(entity_id) {
    let hassio = {
        payload: {
            domain: entity_id.split('.')[0],
            service: 'turn_on',
            target: {
                entity_id: [entity_id]
            }
        },
        topic: entity_id.replace(".", "/") + "/powercontrol/set"
    }

    node.send([null, hassio])
}

/**
 * Spegne il carico
* @param {string} entity_id Entità di Home Assistant
*/
function TurnOff(entity_id) {
    let hassio = {
        payload: {
            domain: entity_id.split('.')[0],
            service: 'turn_off',
            target: {
                entity_id: [entity_id]
            }
        },
        topic: entity_id.replace(".", "/") + "/powercontrol/set"
    }

    node.send([null, hassio])
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
* @param {number} state Stato controllo carichi, 0/1
* @param {string} message Descrizione dello stato
* @param {string} node_status_fill Colore node status
*/
function SendPowerControl(state, message, node_status_fill = 'green') {
    let mqtt = { payload: { attributes: {} } }

    mqtt.payload.state = (state === 0) ? 'OFF' : 'ON'
    mqtt.payload.attributes.app = APP_NAME
    mqtt.payload.attributes.version = APP_VERSION
    mqtt.payload.attributes.author = AUTHOR_NAME
    mqtt.payload.attributes.message = message
    mqtt.payload.attributes.enabled = (PC_ENABLED) ? 'on' : 'off'
    mqtt.payload.attributes.system_running = SYSTEM_RUNNING ? 'on' : 'off'
    mqtt.payload.attributes.system_connected = SYSTEM_CONNECTED ? 'on' : 'off'
    mqtt.payload.attributes.configuration = CONFIGURATION
    mqtt.payload.attributes.delay_on = DELAY_TURN_ON
    mqtt.payload.attributes.delay_off = DELAY_TURN_OFF
    mqtt.payload.attributes.power_max = POWER_MAX
    mqtt.payload.attributes.power_now = POWER_NOW()
    mqtt.payload.attributes.power_available = POWER_AVAILABLE()
    mqtt.payload.attributes.power_limit = POWER_LIMIT
    mqtt.payload.attributes.power_overload = POWER_OVERLOAD()
    mqtt.payload.attributes.overload = IS_OVERLOAD() ? 'on' : 'off'
    mqtt.payload.attributes.overload_critical = IS_OVERLOAD_CRITICAL() ? 'on' : 'off'
    mqtt.payload.attributes.entities_count = ENTITIES_COUNT
    mqtt.payload.attributes.entities_valid = ENTITIES_VALID
    mqtt.payload.attributes.entities_list = ENTITIES_LIST
    mqtt.payload.attributes.entities_energy_consumption = ENTITIES_ENERGY_CONSUMPTION
    mqtt.payload.attributes.entities_energy_pending = ENTITIES_ENERGY_PENDING
    mqtt.payload.attributes.last_updated = GetLocalDateTime()

    //memorizzo i dati
    global.set("powercontrol.settings", mqtt.payload.attributes)

    //conversione payload in stringa
    mqtt.payload = JSON.stringify(mqtt.payload)

    //impostazioni MQTT
    mqtt.qos = 0
    mqtt.retain = true
    mqtt.topic = "info/powercontrol/status"

    //output
    node.send([mqtt, null])

    //node status
    node.status({ fill: node_status_fill, shape: "dot", text: message })
    return true
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
 * Restituisce l'entità di Home Assistant altrimenti un valore null
* @param {string} entity_id Entità di Home Assistant
*/
function GetSystemEntity(entity_id) {
    try {
        return SYSTEM_STATES[entity_id]
    } catch (error) {
        return null
    }
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (!SYSTEM_CONNECTED) {
    SendPowerControl(0, 'Il componente di Node-red "node-red-contrib-home-assistant-websocket" non è connesso', 'red')
    return null
}

if (!SYSTEM_RUNNING) {
    SendPowerControl(0, 'Il componente di Node-red "node-red-contrib-home-assistant-websocket" non è avviato', 'red')
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (IsStringEmpty(SENSOR_GENERAL)) {
    SendPowerControl(0, 'Impostare il sensore generale per la lettura dei consumi', 'red')
    return null
}

if (!SENSOR_GENERAL.startsWith('sensor.')) {
    SendPowerControl(0, 'Il sensore generale impostato non è un`entità di tipo sensor', 'red')
    return null
}

if (POWER_MAX <= 0) {
    SendPowerControl(0, 'Impostare correttamente la potenza contatore, in genere per un`abitazione normale è di 3300 w', 'red')
    return null
} else if (POWER_MAX < 500) {
    SendPowerControl(0, `La potenza del contatore non può essere inferiore a 500 w, attualmente è di ${POWER_MAX} w`, 'red')
    return null
}

if (POWER_LIMIT <= 0) {
    SendPowerControl(0, `Impostare correttamente la potenza limite di sovraccarico che attualmente è di ${POWER_LIMIT} w, il valore consigliato è ${Math.round((POWER_MAX / 100) * 90)} w`, 'red')
    return null
} else if (POWER_LIMIT > POWER_MAX) {
    SendPowerControl(0, `Impostare correttamente la potenza limite di sovraccarico che attualmente è di ${POWER_LIMIT} w e che non può essere superiore a quella del contatore, il valore consigliato è ${Math.round((POWER_MAX / 100) * 90)} w`, 'red')
    return null
}

if (!IsArray(ENTITIES)) {
    SendPowerControl(0, `La lista dei carichi da controllare deve essere di tipo Array, esempio di struttura: [{"power":"entity.name","load":"entity.name"}]`, 'red')
    return null
}

if (ENTITIES_COUNT === 0) {
    SendPowerControl(0, `Nessun carico da controllare`, 'gray')
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
//controllo e validazione delle entità impostate

for (let i = 0; i < ENTITIES.length; i++) {
    let entity = ENTITIES[i]

    //verifico l'esistenza del sensore
    if (entity.power && !SYSTEM_STATES.hasOwnProperty(entity.power)) {
        SendPowerControl(0, `Il sensore ${i} ${entity.power} non esiste in Home Assistant`, 'red')
        return null
    }

    //verifico l'esistenza del carico
    if (entity.load && !SYSTEM_STATES.hasOwnProperty(entity.load)) {
        SendPowerControl(0, `Il carico da controllare ${i} ${entity.load} non esiste in Home Assistant`, 'red')
        return null
    }

    //verifico se il sensore è di tipo sensor
    if (entity.power && !entity.power.startsWith('sensor.')) {
        SendPowerControl(0, `Il sensore ${i} ${entity.power} non è sensore valido`, 'red')
        return null
    }

    //verifico il dominio del carico da controllare
    if (entity.load && !DOMAIN_AVAILABLE.hasOwnProperty(entity.load.split('.')[0])) {
        SendPowerControl(0, `Il carico da controllare ${i} ${entity.load} non è gestito da ${APP_NAME}`, 'red')
        return null
    }

    //validazione e memorizzazione del carico da controllare
    if (entity.load !== null && entity.load !== undefined) {
        ENTITIES[i].state = SYSTEM_STATES[entity.load].state
        ENTITIES[i].name = SYSTEM_STATES[entity.load].attributes.friendly_name
        ENTITIES[i].power = (entity.power) ? entity.power : null
        ENTITIES[i].energy = (entity.power) ? Math.round(SYSTEM_STATES[entity.power].state) : entity.energy || 0
        ENTITIES[i].restore = (typeof entity.restore === 'boolean') ? entity.restore : true
        ENTITIES_ENERGY_CONSUMPTION += ENTITIES[i].energy
        ++ENTITIES_VALID
    }
}

if (ENTITIES_VALID === 0) {
    SendPowerControl(0, `Verificare la lista delle entità perchè non sono state validate`, 'red')
    return null
} else if (ENTITIES_VALID != ENTITIES_COUNT) {
    SendPowerControl(0, `Verificare la lista delle entità perchè ne sono state validate ${ENTITIES_VALID} su ${ENTITIES_COUNT}`, 'red')
    return null
}

global.set("powercontrol.entities", ENTITIES)

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//controllo se è stato impostato un comando per attivare e disattivare il controllo carichi
if (PC_COMMAND_ON_OFF && SYSTEM_STATES.hasOwnProperty(PC_COMMAND_ON_OFF)) {
    let enabled = SYSTEM_STATES[PC_COMMAND_ON_OFF].state

    if (typeof enabled === 'string') {
        PC_ENABLED = (enabled.toLowerCase() == 'on' || enabled.toLowerCase() == 'true')
    } else if (typeof enabled === 'number') {
        PC_ENABLED = (enabled > 0)
    } else if (typeof enabled === 'boolean') {
        PC_ENABLED = enabled
    } else {
        SendPowerControl(0, `Lo stato di ${PC_COMMAND_ON_OFF} non è un valore valido`, 'red')
        return null
    }
} else if (PC_COMMAND_ON_OFF) {
    SendPowerControl(0, `L'entità ${PC_COMMAND_ON_OFF} per attivare o disattivare ${APP_NAME} non esiste in Home Assistant`, 'red')
    return null
} else {
    PC_ENABLED = true
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (PC_ENABLED) {
    ENTITIES_LIST = `Lista dei carichi monitorati:`
} else {
    ENTITIES_LIST = `Lista dei carichi configurati:`
}

for (let i = 0; i < ENTITIES.length; i++) {
    if (ENTITIES[i].state.toLowerCase() == 'on' && ENTITIES[i].energy > 0) {
        ENTITIES_LIST += `\n${(i + 1)}° - ${ENTITIES[i].name} (Acceso ${ENTITIES[i].energy} w)`
    } else if (ENTITIES[i].state.toLowerCase() == 'on' && ENTITIES[i].energy == 0) {
        ENTITIES_LIST += `\n${(i + 1)}° - ${ENTITIES[i].name} (Acceso)`
    } else if (ENTITIES[i].state.toLowerCase() == 'off' && ENTITIES[i].energy > 0) {
        ENTITIES_LIST += `\n${(i + 1)}° - ${ENTITIES[i].name} (Spento ${ENTITIES[i].energy} w)`
    } else if (ENTITIES[i].state.toLowerCase() == 'off' && ENTITIES[i].energy == 0) {
        ENTITIES_LIST += `\n${(i + 1)}° - ${ENTITIES[i].name} (Spento)`
    } else {
        ENTITIES_LIST += `\n${(i + 1)}° - ${ENTITIES[i].name} (${ENTITIES[i].state})`
    }
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

CONFIGURATION = `* ${APP_NAME} ${APP_VERSION} *\n`
CONFIGURATION += `- Contatore: ${POWER_MAX} w\n`
CONFIGURATION += `- Sovraccarico: ${POWER_LIMIT} w\n`
CONFIGURATION += `- Ritardo OFF: ${DELAY_TURN_OFF} m\n`
CONFIGURATION += `- Ritardo ON: ${DELAY_TURN_ON} m\n`
CONFIGURATION += `- Carichi: ${ENTITIES_COUNT}\n`

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (!PC_ENABLED) {
    SendPowerControl(1, `${APP_NAME} è regolarmente configurato ma non è in funzione`, 'gray')
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

var OVERLOAD = {
    overload: false,
    wait: 0,
    energy_consumption: 0,
    energy_pending: 0,
    last_command: GetUnix(),
    entities: []
}

if (IS_OVERLOAD()) {
    OVERLOAD = global.get('powercontrol.overload') || OVERLOAD
    if (!OVERLOAD.overload) {
        OVERLOAD.overload = true
        global.set('powercontrol.overload', OVERLOAD)
    }
} else if (global.get('powercontrol.overload')) {
    OVERLOAD = global.get('powercontrol.overload')
    if (OVERLOAD.overload) {
        OVERLOAD.overload = false
        OVERLOAD.wait = 0
        OVERLOAD.last_command = GetUnix()
        global.set('powercontrol.overload', OVERLOAD)
    }
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

var MINUTES = 0
var SECONDS = 0
var ENERGY_PENDING = 0
var ENTITIES_TURN_OFF = []

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (IS_OVERLOAD()) {
    SECONDS = GetTimeElapsed(OVERLOAD.last_command).seconds
    MINUTES = GetTimeElapsed(OVERLOAD.last_command).minutes
    OVERLOAD.wait = DELAY_TURN_OFF - MINUTES
    OVERLOAD.energy_consumption = ENTITIES_ENERGY_CONSUMPTION

    if (IS_OVERLOAD_CRITICAL() && SECONDS < 30) {
        SendPowerControl(1, `${APP_NAME} ha rilevato un sovraccarico di ${POWER_OVERLOAD()} w e fra ${SECONDS} secondi inizierà a spegnere qualche carico`, 'yellow')
        return null
    } else if (IS_OVERLOAD_CRITICAL() && SECONDS >= 30) {
        OVERLOAD.wait = 0
        OVERLOAD.last_command = GetUnix()

        for (let i = 0; i < ENTITIES.length; i++) {
            let element = ENTITIES[i]
            //cerco i carichi non spenti
            if (element.state.toLowerCase() != 'off') {
                //verifico se ho provato a spegnere il carico, ci riprovo
                if (OVERLOAD.entities.includes(element.load)) {
                    TurnOff(element.load)
                } else {
                    //aggiungo l'energia che sto per spegnere
                    ENERGY_PENDING += element.energy
                    //memorizzo il carico che sto spegnendo
                    OVERLOAD.entities.push(element.load)
                    ENTITIES_TURN_OFF.push(element)
                    TurnOff(element.load)
                    //se l'energia che sto provando a spegnere è sufficente smetto
                    if (ENERGY_PENDING >= POWER_OVERLOAD()) break
                }
            }
        }

        ENTITIES_ENERGY_PENDING = OVERLOAD.energy_pending + ENERGY_PENDING
        OVERLOAD.energy_pending = ENTITIES_ENERGY_PENDING
        global.set('powercontrol.overload', OVERLOAD)

        if (OVERLOAD.entities.length === ENTITIES_COUNT && ENTITIES_ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi per un totale di ${ENTITIES_ENERGY_PENDING} w`, 'yellow')
        } else if (OVERLOAD.entities.length === ENTITIES_COUNT) {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length === 1 && ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF[0].name}, ${ENERGY_PENDING} w`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length > 1 && ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF.length} carichi, ${ENERGY_PENDING} w`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length === 1) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF[0].name}`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length > 1) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF.length} carichi`, 'yellow')
        }

        return null
    } else if (MINUTES >= DELAY_TURN_OFF) {
        OVERLOAD.wait = 0
        OVERLOAD.last_command = GetUnix()

        for (let i = 0; i < ENTITIES.length; i++) {
            let element = ENTITIES[i]
            //cerco i carichi non spenti
            if (element.state.toLowerCase() != 'off') {
                //verifico se ho provato a spegnere il carico, ci riprovo
                if (OVERLOAD.entities.includes(element.load)) {
                    TurnOff(element.load)
                } else {
                    //calcolo l'energia che sto per spegnere
                    ENERGY_PENDING += element.energy
                    //memorizzo il carico che sto spegnendo
                    OVERLOAD.entities.push(element.load)
                    ENTITIES_TURN_OFF.push(element)
                    TurnOff(element.load)
                    break
                }
            }
        }

        ENTITIES_ENERGY_PENDING = OVERLOAD.energy_pending + ENERGY_PENDING
        OVERLOAD.energy_pending = ENTITIES_ENERGY_PENDING
        global.set('powercontrol.overload', OVERLOAD)

        if (OVERLOAD.entities.length === ENTITIES_COUNT && ENTITIES_ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi per un totale di ${ENTITIES_ENERGY_PENDING} w`, 'yellow')
        } else if (OVERLOAD.entities.length === ENTITIES_COUNT) {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length === 1 && ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF[0].name}, ${ENERGY_PENDING} w`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length > 1 && ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF.length} carichi, ${ENERGY_PENDING} w`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length === 1) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF[0].name}`, 'yellow')
        } else if (ENTITIES_TURN_OFF.length > 1) {
            SendPowerControl(1, `${APP_NAME} ha spento ${ENTITIES_TURN_OFF.length} carichi`, 'yellow')
        }

        return null
    } else {
        ENTITIES_ENERGY_PENDING = OVERLOAD.energy_pending

        for (let i = 0; i < ENTITIES.length; i++) {
            if (ENTITIES[i].state.toLowerCase() == 'on' && !OVERLOAD.entities.includes(ENTITIES[i].load)) {
                SendPowerControl(1, `${APP_NAME} ha rilevato un sovraccarico di ${POWER_OVERLOAD()} w e fra ${OVERLOAD.wait} minuti spegnerà ${ENTITIES[i].name}`, 'yellow')
                global.set('powercontrol.overload', OVERLOAD)
                return null
            }
        }

        if (ENTITIES_ENERGY_PENDING > 0) {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi per un totale di ${ENTITIES_ENERGY_PENDING} w`, 'yellow')
        } else {
            SendPowerControl(1, `${APP_NAME} ha spento tutti e ${ENTITIES_COUNT} i carichi`, 'yellow')
        }

        return null
    }
} else {
    MINUTES = GetTimeElapsed(OVERLOAD.last_command).minutes
    OVERLOAD.wait = DELAY_TURN_ON - MINUTES
    OVERLOAD.energy_consumption = ENTITIES_ENERGY_CONSUMPTION

    const ENTITY_NAME = function (/** @type {string} */ entity_id) {
        for (let i = 0; i < ENTITIES.length; i++) {
            if (ENTITIES[i].load == entity_id) {
                return ENTITIES[i].name
            }
        }
        return null
    }

    //verifico ci sono carichi da accende
    if (OVERLOAD.entities.length > 0) {
        for (let i = 0; i < ENTITIES.length; i++) {
            let element = ENTITIES[i]
            //se il carico è già acceso o non deve essere ripristinato lo rimuovo dalla lista
            if (OVERLOAD.entities.includes(element.load) && (element.state.toLowerCase() == 'on' || !element.restore)) {
                //elimino il carico dalla lista
                OVERLOAD.entities = OVERLOAD.entities.filter(function (item) {
                    return item !== element.load
                })
            }
        }
    }

    if (OVERLOAD.entities.length > 0 && MINUTES >= DELAY_TURN_ON) {
        OVERLOAD.wait = 0
        OVERLOAD.last_command = GetUnix()
        let element = OVERLOAD.entities[OVERLOAD.entities.length - 1]
        TurnOn(element)
        global.set('powercontrol.overload', OVERLOAD)
        SendPowerControl(1, `${APP_NAME} ha ripristinato ${element}`, 'yellow')
    } else if (OVERLOAD.entities.length > 1) {
        SendPowerControl(1, `${APP_NAME} fra ${OVERLOAD.wait} minuti ripristina il primo carico`, 'yellow')
        global.set('powercontrol.overload', OVERLOAD)
    } else if (OVERLOAD.entities.length === 1) {
        SendPowerControl(1, `${APP_NAME} fra ${OVERLOAD.wait} minuti ripristina ${ENTITY_NAME(OVERLOAD.entities[0])}`, 'yellow')
        global.set('powercontrol.overload', OVERLOAD)
    } else {
        SendPowerControl(1, `${APP_NAME} è in funzione, il consumo attuale è ${POWER_NOW()} w su una potenza disponibile di ${POWER_AVAILABLE()} w`, 'green')
        global.set('powercontrol.overload', null)
    }

    return null
}

